import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "../src/styles/App.css";
import Header from "./components/Header/Header";
import Results from "../src/components/Results/Results";
import ResultsProductDetails from "../src/components/Results/ResultsProductDetails";
//import ProductDetail from "./components/Results/ProductDetail";

function App() {
  // Arreglo de datos, inicialmente vacio//
  const [data, setData] = useState([]);
  //valor del boton de busqueda, se actualiza en cada onChange//
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch(`http://localhost:7000/api/items?q=${search}`)
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((error) => console.error(error));
  }, [search]);


const setResultsData = (data) => {
  setData
}

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Header
                  setSearch={setSearch}
                  search={search}
                  setData={setData}
                />
                <Results data={data} />
              </>
            }
          />

          {/* <Route path="/" element={<Navigate to="/items?search=" />} /> */}

          <Route path="/items/:itemid" element={<ResultsProductDetails />} />
        </Routes>
        {/* <Results data={data} /> */}
      </Router>
    </div>
  );
}

export default App;
