import React, { useState, useEffect } from "react";
import { BrowserRouter,  } from "react-router-dom";
import "../src/styles/App.css";
import Header from "./components/Header/Header";
import Results from "../src/components/Results/Results";
import ProductDetail from "../src/components/Results/ProductDetail";

function App() {
  // Arreglo de datos, inicialmente vacio//
  const [data, setData] = useState([]);
  //valor del boton de busqueda, se actualiza en cada onChange//
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch(`http://localhost:7000/api/items?q=${search}`)
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((error) => console.error(error));
  }, [search]);

  return (
    <BrowserRouter>
      <div className="App">
        <Route exact path="/" component= {<Header setSearch={setSearch} search={search} setData={setData} />}/>


        <Results data={data} setData={setData} />


      </div>
    </BrowserRouter>
  );
}

export default App;
