import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Outlet,
} from "react-router-dom";
import "../src/styles/App.css";
import Header from "./components/Header/Header";
import Results from "../src/components/Results/Results";
import ResultsProductDetails from "../src/components/Results/ResultsProductDetails";

function App() {
  // Arreglo de datos, inicialmente vacio//
  const [data, setData] = useState([]);
  //valor del boton de busqueda, se actualiza en cada onChange//
  const [search, setSearch] = useState("");
  // Estado de cargando
  const [loader, setLoader] = useState(false);
  //Estado de error
  const [error, setError] = useState(false);

  // useEffect(() => {
  //   fetch(`http://localhost:7000/api/items?q=${search}`)
  //     // setLoader(true)
  //     .then((res) => res.json())
  //     .then((data) => setData(data))
  //     .catch((error) => console.error(error));
  //   // setLoader(false);
  // }, [search]);

  const setResultsData = (data) => {
    setData(data);
  };

  const setSearchResult = (data) => {
    setSearch(data);
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Header
                  setSearch={setSearchResult}
                  search={search}
                  setData={setResultsData}
                  setLoader={setLoader}
                  setError={setError}
                />
              </>
            }
          ></Route>
          <Route
            path="/items"
            element={

                <Outlet />

            }
          >
            <Route path=":itemid" element={<ResultsProductDetails />} />
            <Route
              index
              element={<Results data={data} loader={loader} error={error} />}
            />
          </Route>
        </Routes>
      </Router>
    </div>
  );
}

export default App;
