import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import "../src/styles/App.css";
import Header from "./components/Header/Header";
import Results from "../src/components/Results/Results";
import ResultsProductDetails from "../src/components/Results/ResultsProductDetails";

function App() {
  // Arreglo de datos, inicialmente vacio//
  const [data, setData] = useState([]);
  //valor del boton de busqueda, se actualiza en cada onChange//
  const [search, setSearch] = useState("");
  //arreglo de items
  const [itemid, setItemid] = useState([]);
  //Description
  //const [description, setDescription] = useState("");

  useEffect(() => {
    fetch(`http://localhost:7000/api/items?q=${search}`)
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((error) => console.error(error));
  }, [search]);

  useEffect(() => {
    fetch(`http://localhost:7000/api/items/${itemid}`)
      .then((res) => res.json())
      .then((data) => console.log("data:", data))

      .catch((error) => console.log(error));
  }, [itemid]);

  // useEffect(() => {
  //   fetch (`https://localhost:7000/api.mercadolibre.com/items/${itemid}/description`)
  //   .then((res) => res.json())
  //     .then((data) => setData(data))
  //     .catch((error) => console.log(error));
  // }, [third])

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          {/* Caja de Búsqueda: ​“/” */}
          <Route path="/" element={<Header setSearch={setSearch} search={search} setData={setData} /> }/>
          {/* Resultados de la búsqueda:​“/items?search=” */}
          <Route path="/detalles" element={<ResultsProductDetails />} />

          {/* RDetalle del producto: ​“/items/:id” */}
          <Results data={data} setData={setData} setItemid={setItemid} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;

