import React, { useState, useEffect } from "react";
import ProductDetail from "../../components/Results/ProductDetail";

//esta es la que voy a enrutar al dar click y dar detalle del producto

function ResultsProductDetails() {
  //data item
  const [item, setItem] = useState([]);
  //arreglo de items
  const [itemid, setItemid] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:7000/api/items/${itemid}`)
      .then((res) => res.json())
      .then((data) => setItem(data))
      .catch((error) => console.log(error));
  }, [it]);
  setItemid();

  return (
    <section>
      <div>
        <p className="categoryContentProduct">category</p>
        <div className="contencCardComplete">
          {item && (
            <ProductDetail
              key={item.id}
              title={item.title}
              price={item.price.amount}
              price_decimals={item.price.decimals}
              price_currency={item.price.currency_id}
              picture={item.thumbnail}
              condition={item.condition}
              free_shipping={item.shipping.free_shipping}
              sold_quantity={item.old_quantity}
              // description={description.data.plain_text}
            />
          )}
        </div>
      </div>
    </section>
  );
}

export default ResultsProductDetails;
