import React, { useState, useEffect } from "react";
import ProductDetail from "../../components/Results/ProductDetail";

//esta es la que voy a enrutar al dar click y dar detalle del producto

function ResultsProductDetails ({ data }) {
  //arreglo de items
  const [itemid, setItemid] = useState([]);


  useEffect(() => {
    fetch(`http://localhost:7000/api/items/${itemid}`)
      .then((res) => res.json())
      .then((data) => setItemid(data))
      .catch((error) => console.log(error));
  }, []);

  return (
    <section>
      <div>
        <p className="categoryContentProduct">category</p>
        <div className="contencCardComplete">
          {data?.item?.map((data) => {
            return (
              <ProductDetail
                key={data.id}
                title={data.title}
                price={data.price.amount}
                price_decimals={data.price.decimals}
                price_currency={data.price.currency_id}
                picture={data.thumbnail}
                condition={data.condition}
                free_shipping={data.shipping.free_shipping}
                sold_quantity={data.old_quantity}
                // description={description.data.plain_text}
              />
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default ResultsProductDetails;
